import React, { Component } from 'react'
import banner from '../img/banner6.jpg'
import styled from 'styled-components';


export default class Banner extends Component {
    render() {
        return (
            <div className="container-fluid px-0">
                <img id="banner" className="fw-100 mh-75 img-fluid" src={banner}></img>
                <div class="centered">
                    <StartButton as="a" href="#productSection">Get Started</StartButton>
                </div>
            </div>
        )
    }
}

const StartButton = styled.button`
text-transform:capitalize;
font-size:1.4rem;
background:transparent;

border: 0.2rem solid;
border-image-source:"linear-gradient(45deg, #ed5565, #d52739)";
border-image-slice: 1;
color: ${
    props => props.cart?"var(--btnBlue)":
    "var(--mainRed)"
};
border-radius: .5rem;
padding: 0.2rem 0.5rem;
cursor:pointer;
margin:0.2rem 0.5rem 0.2rem 0;
transition: all 0.5s ease-in-out;
&:hover{
    background-image: ${
        props => props.cart?"linear-gradient(45deg, #5d9bec, #0d64d7)":
        "linear-gradient(45deg, #ed5565, #d52739)"
    };

    color: white;
}
&: focus{
    outline:none;
}

`;
